solution.image:
>


            \tikzset{%
            }
            \begin{tikzpicture}
            \begin{axis} [
            xmin=-1, xmax=6,
            grid=both, minor tick num={1},
            ]
            \addplot[no marks, black] coordinates {
            (-1,0)
            (6,0)
            };
            \addplot[no marks, blue, thick] coordinates {
            (-1,4)
            (1,4)
            (4,-2)
            (6, 0)
            };
            \draw [thick, black] (axis cs: 0,0) -- (axis cs: 0,4);
            \draw [thick, dashed, black] (axis cs: 1,0) -- (axis cs: 1,4);
            \draw [thick, black] (axis cs: 5,0) -- (axis cs: 5,-1);
            \draw [thick, dashed, black] (axis cs: 4,0) -- (axis cs: 4,-2);
            \fill [opacity=0.25, blue] (axis cs:0,4) -- (axis cs:1,4)
            -- (axis cs:3,0) -- (axis cs:0,0) -- cycle;
            \fill [opacity=0.25, red] (axis cs:3,0) -- (axis cs:4,-2)
            -- (axis cs: 5,-1) -- (axis cs: 5,0) -- cycle;
            \end{axis}
            \end{tikzpicture}

