jsxgraph.input:

var jcf1 = { 
  f : function(x) 
    {
      var y=40-16*x*x;
      return y;
     },
  input : { name:'t', color:'blue', value:1, delta:0.25, fixed:true, min:0.67, max:1.33 },
  output : { name:'h', color:'red', min:10, max:35 },
};
createMapRateOfChange(jcf1, 'jsx-calc-overview-rate-of-change');

