\begin{axis}
[
  xmin=-3, xmax=3, ymin=-4, ymax=4,
  axis lines = center, xlabel = {$x$}, ylabel={$y$},
]
\addplot [ domain=-3:3, thick, blue ] {x^2-2};
\addplot [ name path=average, domain=0:2, samples=50, thick, red ] {-2/3};
