var jcf1 = {
  f : function(x)
    {
      var y=undefined;
      if (Math.abs(x-Math.floor(x)) < 1e-2)
        y=2*x+5;
      return y;
     },
  input : { name:'n', color:'blue', value:0, min:-2.5, max:2.5 },
  output : { name:'xn', color:'red', min:-0.5, max:10.5 },
};
createMap(jcf1, 'jsx-sequence-as-map');


