jsxgraph.input:

var idName = "jsx-fcn-behavior-concavity-ex1";
var jcf4 = {
  input: { name:"x", min:-5.5, max:5.5 },
  output: { name:"y", min:-2, max:20 },
};
jcf4.b = JXG.JSXGraph.initBoard(idName, 
  {
    boundingbox:[
        jcf4.input.min,
        jcf4.output.max,
        jcf4.input.max,
        jcf4.output.min
      ],
    axis: true,
    grid: false,
    showCopyright:false,
    showNavigation:false,
  }
);
jcf4.plot = jcf4.b.create("functiongraph", 
  function(x) { return x*x; }
);
jcf4.x1 = jcf4.b.create("glider",
  [-4, 16, jcf4.plot],
  { name:"A" },
);
jcf4.x2 = jcf4.b.create("glider",
  [-2, 4, jcf4.plot],
  { name:"B" },
);
jcf4.x3 = jcf4.b.create("glider",
  [-1, 1, jcf4.plot],
  { name:"C" },
);
jcf4.b.create("segment", [jcf4.x1, jcf4.x2]);
jcf4.b.create("segment", [jcf4.x2, jcf4.x3]);
jcf4.mAB = jcf4.b.create("text",
  [-5, 3.5, function() {
    var deltaY = jcf4.x2.Y()-jcf4.x1.Y(),
    deltaX = (jcf4.x2.X()-jcf4.x1.X());
    return "m_{AB} = " + Number(deltaY/deltaX).toFixed(3)
  }]
);
jcf4.mBC = jcf4.b.create("text",
  [-5, 2.5, function() {
    var deltaY = jcf4.x3.Y()-jcf4.x2.Y(),
    deltaX = (jcf4.x3.X()-jcf4.x2.X());
    return "m_{BC} = " + Number(deltaY/deltaX).toFixed(3)
  }]
);
jcf4.mCompare = jcf4.b.create("text",
  [-5,1.5, function() {
    var deltaX, deltaY, mAB, mBC;
    deltaY = jcf4.x2.Y()-jcf4.x1.Y(),
    deltaX = (jcf4.x2.X()-jcf4.x1.X());
    mAB = deltaY/deltaX;
    deltaY = jcf4.x3.Y()-jcf4.x2.Y(),
    deltaX = (jcf4.x3.X()-jcf4.x2.X()),
    mBC = deltaY/deltaX;
    var cmpStr = "";
    if (jcf4.x1.X() > jcf4.x2.X() || jcf4.x2.X() > jcf4.x3.X()) {
      cmpStr = "Need A < B < C";
    } else {
      if (mAB > mBC) {
        cmpStr = "m_{AB} > m_{BC}";
      } else if (mAB < mBC) {
        cmpStr = "m_{AB} < m_{BC}";
      }
    }
    return cmpStr;
  }]
);


