  }]
);
jcf5.mBC = jcf5.b.create("text",
  [-3, 1.5, function() {
    var deltaY = jcf5.x3.Y()-jcf5.x2.Y(),
    deltaX = (jcf5.x3.X()-jcf5.x2.X());
    return "m_{BC} = " + Number(deltaY/deltaX).toFixed(3)
  }]
);
jcf5.mCompare = jcf5.b.create("text",
  [-3,1, function() {
    var deltaX, deltaY, mAB, mBC;
    deltaY = jcf5.x2.Y()-jcf5.x1.Y(),
    deltaX = (jcf5.x2.X()-jcf5.x1.X());
    mAB = deltaY/deltaX;
    deltaY = jcf5.x3.Y()-jcf5.x2.Y(),
    deltaX = (jcf5.x3.X()-jcf5.x2.X()),
    mBC = deltaY/deltaX;
    var cmpStr = "";
    if (jcf5.x1.X() >= jcf5.x2.X() || jcf5.x2.X() >= jcf5.x3.X()) {
      cmpStr = "Need A < B < C";
    } else {
      if (mAB > mBC) {
        cmpStr = "m_{AB} > m_{BC}";
      } else if (mAB < mBC) {
        cmpStr = "m_{AB} < m_{BC}";
      }
    }
    return cmpStr;
  }]
);

