jsxgraph.input:
var jcf = {};
JXG.Options.axis.ticks.majorHeight = 0.5;
jcf.b = JXG.JSXGraph.initBoard('jsxgraph-constant-function-ex1', 
            {
                boundingbox:[-5,3,5,-1],
                axis: false,
                grid: false,
                showCopyright:false,
                showNavigation:false,
            });
jcf.xaxis = jcf.b.create('axis',
    [ [0,2],[1,2] ], {
     drawZero:true // Doesn't do anything here.
    }
);
jcf.yaxis = jcf.b.create('axis',
    [ [0,0],[1,0] ], {
     drawZero:true // Doesn't do anything here.
    }
);
jcf.xV = jcf.b.create('glider', [0,2, jcf.xaxis],
                    {
                    name:'x',
                    });
jcf.yV = jcf.b.create('point', [3,0],
                    {
                    name:'y',
                    fixed:true,
                    color:'blue',
                    });
jcf.fArrow = jcf.b.create('arrow', [jcf.xV, jcf.yV],
                    {
                    name: 'f',
                    fixed:true,
                    color:'black',
                    });

var jcf = {};
JXG.Options.axis.ticks.majorHeight = 0.5;
jcf.b = JXG.JSXGraph.initBoard('jsxgraph-identity-function-ex2', 
            {
                boundingbox:[-5,3,5,-1],
                axis: false,
                grid: false,
                showCopyright:false,
                showNavigation:false,
            });
jcf.xaxis = jcf.b.create('axis',
    [ [0,2],[1,2] ], {
     drawZero:true // Doesn't do anything here.
    }
);
jcf.yaxis = jcf.b.create('axis',
    [ [0,0],[1,0] ], {
     drawZero:true // Doesn't do anything here.
    }
);
jcf.xV = jcf.b.create('glider', [0,2, jcf.xaxis],
                    {
                    name:'x',
                    });
jcf.yV = jcf.b.create('point', [function() { return jcf.xV.X() },0],
                    {
                    name:'y',
                    fixed:true,
                    color:'blue',
                    });
jcf.fArrow = jcf.b.create('arrow', [jcf.xV, jcf.yV],
                    {
                    name: 'f',
                    fixed:true,
                    color:'black',
                    });
