\tikzset{%
}
\begin{tikzpicture}
    \begin{axis}[
        xmin=-6,xmax=6,
        ymin=-4,ymax=4,
        xlabel={$x$},
        ylabel={$y$},
    ]

    \addplot[blue, domain=-6:6, thick, samples=100]
        ({x}, {(-3*x+sqrt(300-11*x^2))/10});
    \addplot[blue, domain=-6:6, thick, samples=100]
        ({x}, {(-3*x-sqrt(300-11*x^2))/10});
    \addplot[blue, domain=-6:6, thick, samples=100]
        ({(-3*x+sqrt(60-11*x^2))/2}, {x});
    \addplot[blue, domain=-6:6, thick, samples=100]
        ({(-3*x-sqrt(60-11*x^2))/2}, {x});
    \addplot[mark=*, blue] coordinates {(2,1)};
    \end{axis}
\end{tikzpicture}


